void calbk(timeHandle_t htim){
	if(timer.capture.ccr < 0){ return; }
	irq_flag = 1;
	rpm = 60 * (1 / ((timer.capture.ccr * ticks) * 0.000001));
	if(rpm > 2000){ rpm = 1617; }
	// if(rpm < 0){ return; }

	// rpm -= 25;
	rpm -= 25;

	// totalDegree = (rpm / 60) * 360;
	totalDegree = (rpm / 60) * 364;
	// totalDegree = (rpm / 60) * 350;

	// totalDegree -= 250;
	totalDegree -= 560;

	totalDegree = moving_average_total(totalDegree);

	array[0 ] = (((17.61 / totalDegree) / 16) * 1000000) + 5; 
	array[1 ] = (((18.21 / totalDegree) / 16) * 1000000) + 4;
	array[2 ] = (((18.87 / totalDegree) / 16) * 1000000) + 3;
	array[3 ] = (((19.57 / totalDegree) / 16) * 1000000) + 2;
	array[4 ] = (((20.33 / totalDegree) / 16) * 1000000) + 1;
	array[5 ] = (((21.15 / totalDegree) / 16) * 1000000) - 0;
	array[6 ] = (((22.04 / totalDegree) / 16) * 1000000) - 1;
	array[7 ] = (((23.01 / totalDegree) / 16) * 1000000) - 2;
	array[8 ] = (((24.07 / totalDegree) / 16) * 1000000) - 3;
	array[9 ] = (((25.23 / totalDegree) / 16) * 1000000) - 4;
	array[10] = (((26.51 / totalDegree) / 16) * 1000000) - 5;
	array[11] = (((27.93 / totalDegree) / 16) * 1000000) - 6;
	array[12] = (((29.51 / totalDegree) / 16) * 1000000) - 7;
	array[13] = (((31.29 / totalDegree) / 16) * 1000000) - 7;

	int pA = (34.60 / totalDegree) * 1000000;
	int pB = (17.60 / totalDegree) * 1000000;
	adjustDelay = pA - (pB / 2) - 690;
}



typedef struct {
	gpio_t red;
	gpio_t green;
	gpio_t blue;
} octaDisplay_t;


octaDisplay_t display;

void setColor(int red, int green, int blue){
	gpinSet(&display.red, red ? PIN_ON : PIN_OFF);
	gpinSet(&display.green, green ? PIN_ON : PIN_OFF);
	gpinSet(&display.blue, blue ? PIN_ON : PIN_OFF);
}

void allColorOff(){
	gpinSet(&display.red, PIN_OFF);
	gpinSet(&display.green, PIN_OFF);
	gpinSet(&display.blue, PIN_OFF);
}


