void TIM2_Init_er(void) {
	// Enable clock for TIM2
	RCC->APB1ENR |= RCC_APB1ENR_TIM2EN;

	// Set prescaler to get 1 µs per tick (assuming 72 MHz APB1 clock)
	TIM2->PSC = 72 - 1;  // Prescaler (PSC) = 72 - 1 → Timer clock = 1 MHz (1 µs per tick)

	// Set auto-reload register to maximum
	TIM2->ARR = 0xFFFF;  

	// Enable TIM2
	TIM2->CR1 |= TIM_CR1_CEN;
}

void delay_us(uint16_t us) {
    TIM2->CNT = 0;  // Reset counter
    while (TIM2->CNT < us);  // Wait until count reaches delay time
}



int main(void){
	TIM2_Init_er();
	boardInit();
	uart_t uart = uartInit(UART3, 115200);
	uartSetSTDOUT(&uart);

	timerCaptureInit(&timer, B5_TIM3_CH2, CAPTURE_FALLIGN, ticks, calbk);


	gpio_t pinOut = gpinInit(B_4, GPIO_OUTPUT_MODE, GPIN_NO_PULL);
	gpinSet(&pinOut, PIN_ON);

	display.blue = gpinInit(B_12, GPIO_OUTPUT_MODE, GPIN_PUSH_PULL);
	display.green = gpinInit(B_13, GPIO_OUTPUT_MODE, GPIN_PUSH_PULL);
	display.red = gpinInit(B_14, GPIO_OUTPUT_MODE, GPIN_PUSH_PULL);


	int j = 0;

	// for(int i = 0; i < MAX_BUF_SIZE; i++){ displayBuffer[i] = 0; }

	while(1){
		while(irq_flag == 0){ __asm__ volatile ("nop"); }

		// delayUs(adjustDelay);
		delay_us(adjustDelay);

		if(opto == 15){
			cntr ++;
			opto = 0;
		}
		opto++;

		if(cntr == (sizeof(buffer) / sizeof(buffer[0])) - 1){ cntr = 0; }
		
		memcpy(displayBuffer, buffer[cntr], 672);

		j = 0;
		for(int i = 0; i < 14; i++){
			int delayVal = array[i] - 12;
			for (int k = 0; k < 48; k += 3) {
				setColor(displayBuffer[j + k], displayBuffer[j + k + 1], displayBuffer[j + k + 2]);
				// delayUs(delayVal);
				delay_us(delayVal);
			}

			setColor(0, 0, 0);  // None
			j += 48;
		}

		irq_flag = 0;
	}

	return 0;
}

